sysauto_script.script:
var howLong=gs.getProperty('x_58872_needit.autoCloseOverdue');
var grNeedTask= new GlideRecord('x_58872_needit_needit_task');
grNeedTask.addQuery('due_date','<=',gs.daysAgo(howLong) );
grNeedTask.addQuery('state','<',3);
grNeedTask.query();
while(grNeedTask.next()){
	gs.info('Auto close NeedIt task record number is ' + grNeedTask.number);
	grNeedTask.state=4;
	grNeedTask.work_notes='Record closed because it is too overdue';
	grNeedTask.update();

}
